namespace Netvlies\Bundle\PublishBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * CommandLog repository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CommandLogRepository extends EntityRepository
{
    /**
     * @param $application
     * @param null $limit
     * @return mixed
     */
    public function getLogsForApplication($application, $limit=null)
    {
        $entityManager = $this->getEntityManager();

        $query = $entityManager->createQuery('
            SELECT c FROM Netvlies\Bundle\PublishBundle\Entity\CommandLog c
            WHERE c.application = :app
            ORDER BY c.id DESC
        ')->setParameter('app', $application);


        if(!is_null($limit)){
            $query->setMaxResults($limit);
        }

        return $query->getResult();
    }


    /**
     * @param Target $target
     * @return mixed
     */
    public function countLogsForTarget(Target $target)
    {
        $entityManager = $this->getEntityManager();

        $query = $entityManager->createQuery('
            SELECT COUNT(c) FROM Netvlies\Bundle\PublishBundle\Entity\CommandLog c
            WHERE c.target = :target
        ')->setParameter('target', $target);

        return $query->getSingleScalarResult();
    }

    /**
     * @param Target $target
     * @return mixed
     * @throws \Doctrine\ORM\NoResultException
     * @throws \Doctrine\ORM\NonUniqueResultException
     */
    public function getLastDeployment(Target $target)
    {
        $entityManager = $this->getEntityManager();

        $query = $entityManager->createQuery('
            SELECT c FROM Netvlies\Bundle\PublishBundle\Entity\CommandLog c
            WHERE c.target = :target
            ORDER BY c.id DESC
        ')
        ->setParameter('target', $target);

        $query->setMaxResults(1);

        return $query->getSingleResult();
    }

    /**
     * @return mixed
     * @throws \Doctrine\ORM\NoResultException
     * @throws \Doctrine\ORM\NonUniqueResultException
     */
    public function getLatestDeployments()
    {
        $entityManager = $this->getEntityManager();

        $query = $entityManager->createQuery('
            SELECT c FROM Netvlies\Bundle\PublishBundle\Entity\CommandLog c
            ORDER BY c.id DESC
        ');

        $query->setMaxResults(5);

        return $query->getResult();
    }

    /**
     * @param $application
     * @return mixed
     */
    public function getFirstDeployment($application)
    {
        $entityManager = $this->getEntityManager();

        $query = $entityManager->createQuery('
            SELECT c FROM Netvlies\Bundle\PublishBundle\Entity\CommandLog c
            WHERE c.application = :app
            ORDER BY c.id ASC
        ')->setParameter('app', $application);

        $query->setMaxResults(1);
        $result = $query->getResult();

        if (empty($result)) {
            return null;
        }

        return array_pop($result);
    }

    /**
     * @param $application
     * @return mixed
     */
    public function getTimesDeployed($application)
    {
        return $this->createQueryBuilder('c')
            ->select('count(c.id)')
            ->where('c.application = :app')
            ->setParameter('app', $application)
            ->getQuery()
            ->getSingleScalarResult();
    }

    /**
     * @param $application
     * @return mixed
     */
    public function getTimesSuccessfulDeployed($application)
    {
        return $this->createQueryBuilder('c')
            ->select('count(c.id)')
            ->where('c.application = :app')
            ->andWhere("c.exitCode = '0'")
            ->setParameter('app', $application)
            ->getQuery()
            ->getSingleScalarResult();
    }

    /**
     * @param $application
     * @return mixed
     */
    public function getDeployers($application)
    {
        return $this->createQueryBuilder('c')
            ->select('distinct(c.user)')
            ->where('c.application = :app')
            ->setParameter('app', $application)
            ->getQuery()
            ->getResult();
    }

    /**
     * @param string $user
     * @param int $limit
     * @return array
     * @throws \Doctrine\DBAL\DBALException
     */
    public function getFavouriteApplications($user='anonymous', $limit=5)
    {
        $entityManager = $this->getEntityManager();

        $sql = '
            SELECT
              c.application_id,
              MAX(c.datetimestart) AS datum
            FROM
              CommandLog c
              INNER JOIN Application a ON c.application_id = a.id
            WHERE
              c.user = ?
              AND a.status = ?
            GROUP BY
              c.application_id
            ORDER BY datum DESC
            LIMIT 5';

        $statement = $entityManager->getConnection()->prepare($sql);
        $statement->bindValue(1, $user);
        $statement->bindValue(2, Application::STATUS_ACTIVE);
        $statement->execute();
        $result = $statement->fetchAll();
        $appIds = array();
        $apps = array();

        foreach($result as $result){
            $appIds[] = $result['application_id'];
            $apps[] = $this->getEntityManager()->getRepository('NetvliesPublishBundle:Application')->findOneById($result['application_id']);
        }

        if(count($appIds) < $limit && count($appIds) > 0){

            $query = $entityManager->createQuery('
               SELECT a FROM Netvlies\Bundle\PublishBundle\Entity\Application a
               WHERE a.id NOT IN (:appIds)
               AND a.status = :status
               ORDER BY a.id DESC'
            )
                ->setParameter('appIds', empty($appIds)? array(0) : $appIds)
                ->setParameter('status', Application::STATUS_ACTIVE);


            $query->setMaxResults($limit - count($appIds));
            $result = $query->getResult();

            foreach($result as $app){
                $apps[] = $app;
            }
        }

        return $apps;
    }

}
